="widget-title"> <span class="icon" style="width: 15px;"> <i class="icon-th"></i> </span>                                                <h5>Plant Allocation</h5>                                            </div>                                            <div class="widget-content nopadding" style="height: 200px; overflow: auto;">                                                <table class="table table-bordered table-striped with-check">                                                    <thead>                                                        <tr>                                                            <th></th>                                                            <th>Reference</th>                                                            <th>Style</th>                                                            <th>Quantity</th>                                                            <th>Required MOs</th>                                                            <th>Delivery Date</th>                                                            <th>Allocated To</th>                                                        </tr>                                                    </thead>                                                    <tbody>                                                        <?php                                                        $result1 = $Planning->getReferencedPO();                                                        if (mysqli_num_rows($result1) == 0) {                                                            ?>                                                            <tr>                                                                <td colspan="6">No references available for allocation</td>                                                            </tr>                                                            <?php                                               