 }                                        };                                        var references = new Array();                                        var ref = document.getElementsByName("reference[]");                                        var j = 0;                                        for (var i = 0; i < ref.length; i++) {                                            if (ref[i].checked) {                                                references[j] = ref[i].value;                                                j++;                                            }                                        }                                        var r = references.join();                                        var p = document.plantAllocation.plant_no.value;                                        r = encodeURIComponent(r);                                        p = encodeURIComponent(p);                                        xmlhttp.open("GET", "Ajax/plant-allocation.php?p=" + p + "&r=" + r, true);                                        xmlhttp.send();                                        document.getElementById("modelMyAlert").click();                                    }                                </script>                            </div>                        </div>                        <div class="widget-content">                            <div id="myAlert" class="modal hide">                            </div>                        </div>                        <script>                            function submitAlloForm() {                                document.plantAllocation.submit();                            }                        </script>                    </div>                </div>            </div>        </div>        <!--Footer-part-->        <?php        include 'scripts.php';        ?>    </body></html>