xmlhttp.send();                                            return false;                                        }                                    </script>                                <div id="report"></div>                            </div>                        </div>                        <div class="row">                            <div class="span9"></div>                            <div class="span3">                                <table width="100%" border="1" class="text-center">                                    <tr>                                        <td><a>1</a></td>                                        <td><a>2</a></td>                                        <td><a>3</a></td>                                        <td><a>4</a></td>                                        <td><a>5</a></td>                                        <td><a>6</a></td>                                        <td><a>7</a></td>                                    </tr>                                </table>                            </div>                        </div>                    </div>                </div>            </div>            <!--Footer-part-->            <?php            include 'scripts.php';            ?>    </body></html>