e="<?php echo date('Y-m-d');?>" required="">                                        </div>                                    </div>                                    <div class="control-group">                                        <label class="control-label">Session :</label>                                        <div class="controls">                                            <select class="span3" name="session" reequired="">                                                <!--<option value="" disabled="" selected="">Select Session</option>-->                                                <?php                                                $j = 1;                                                while ($j < 16) {                                                    if ($j < 10) {                                                        $j = '0' . $j;                                                    }                                                    $result = $General->getSessionTime($j);                                                    $array = mysql_fetch_array($result);                                                    $time = date('h:i a', strtotime($array['time']));                                                    ?>                                                    <option value="<?php echo $j; ?>">Session <?php echo $j; ?> -  &nbsp;&nbsp;<?php echo $time; ?></option>                                                    <?php                                                    $j++;                                                }                                                ?>                                            </select>                                            <div class="span3">                                            <input type="submit" class="btn btn-success pull-right" onclick="return(getInputs())">                                            </div><br>                                        </div><br>