                        for (var i = 0; i < pos.length; i++) {                                            if (pos[i].checked) {                                                po_numbers[j] = pos[i].value;                                                j++;                                            }                                        }                                        var p = po_numbers.join();                                        var r = document.lineAllocation.reference.value;                                        r = encodeURIComponent(r)                                        p = encodeURIComponent(p);                                        xmlhttp.open("GET", "Ajax/reference-allocation.php?p=" + p + "&r=" + r, true);                                        xmlhttp.send();                                        document.getElementById("modelMyAlert").click();                                    }                                </script>                            </div>                        </div>                        <div class="widget-content">                            <div id="myAlert" class="modal hide">                            </div>                        </div>                        <script>                            function submitAlloForm() {                                document.lineAllocation.submit();                            }                        </script>                    </div>                </div>            </div>        </div>        <!--Footer-part-->        <?php        include 'scripts.php';        ?>    </body></html>